// - ------------------------------------------------------------------------------------------ - //
#include "lzma.h"
// - ------------------------------------------------------------------------------------------ - //
#include <GEL/RawBlock/RawBlock_Core.h>
// - ------------------------------------------------------------------------------------------ - //
void *alloc_LZMA(void*, SizeT size) { 
	return reinterpret_cast<void*>(rawBlock_New(size)->data);
}
void free_LZMA(void*, void* address) { 
	rawBlock_Delete((RawBlock*)((char*)(address) - sizeof(RawBlock)));
}
// - ------------------------------------------------------------------------------------------ - //

// - ------------------------------------------------------------------------------------------ - //
// VTable for allocating the memory LZMA needs
ISzAlloc lzma_alloc_struct = { alloc_LZMA, free_LZMA };
// - ------------------------------------------------------------------------------------------ - //
